SELECT api_perkuliahan.nim_id, api_mahasiswa.nama, api_mahasiswa.alamat,  api_mahasiswa.tanggal_lahir, api_matakuliah.kode_mk, api_matakuliah.nama_mk, api_matakuliah.sks, api_perkuliahan.nilai FROM api_perkuliahan JOIN api_mahasiswa  ON api_mahasiswa.nim=api_perkuliahan.nim_id JOIN api_matakuliah  ON api_matakuliah.kode_mk=api_perkuliahan.kode_mk_id;